/* Trigger structure for table `adiwiyata` */

DELIMITER $$

/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `kuisioner` */$$

/*!50003 CREATE */ /*!50017 DEFINER = 'aya'@'localhost' */ /*!50003 TRIGGER `kuisioner` AFTER INSERT ON `adiwiyata` FOR EACH ROW BEGIN
	INSERT INTO adiwiyata_kuisioner
	(adiwiyata_id, tpl_kuisioner_id, judul, keterangan, created_at, created_by)
	(
	SELECT NEW.id, k.id, k.judul, k.keterangan, NEW.created_at, NEW.created_by FROM kuisioner k WHERE k.is_active = 1 LIMIT 1
	);
	
	INSERT INTO adiwiyata_kuisioner_komponen
	(kuisioner_id, tpl_komponen_id, nama_indikator, created_at, created_by)
	(
	SELECT (SELECT a.id FROM adiwiyata_kuisioner a WHERE a.adiwiyata_id = NEW.id LIMIT 1), k.id as kid, k.nama_indikator, NEW.created_at, NEW.created_by FROM kuisioner_komponen k
	);
	
	cALL sp_clone_pertanyaan(NEW.id);
	
	call sp_clone_pilihan(NEW.id);
	
	call sp_clone_jawaban(NEW.id);
    END */$$


DELIMITER ;

/* Procedure structure for procedure `sp_clone_jawaban` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_clone_jawaban` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`aya`@`localhost` PROCEDURE `sp_clone_jawaban`(a_adiwiyata_id INT)
BEGIN
	DECLARE num_rows INT;
	DECLARE no_more_rows BOOL DEFAULT FALSE;
	DECLARE id_pilihan INT;
	DECLARE created_date DATETIME;
	DECLARE created_user INT;
	
	DECLARE sch_cur CURSOR FOR 
        SELECT a.id, a.created_at, a.created_by FROM adiwiyata_kuisioner_pertanyaan_pilihan a
	INNER JOIN adiwiyata_kuisioner_pertanyaan b ON a.pertanyaan_id = b.id
	INNER JOIN adiwiyata_kuisioner_komponen c ON b.komponen_id = c.id
	inner join adiwiyata_kuisioner d ON c.kuisioner_id = d.id
	WHERE d.adiwiyata_id = a_adiwiyata_id;
	
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET no_more_rows = TRUE;
	
	OPEN sch_cur;
	SET @now_time := NOW();
	SELECT FOUND_ROWS() INTO num_rows;
	the_loop: LOOP
        FETCH sch_cur
        INTO id_pilihan, created_date, created_user;
        
        IF no_more_rows THEN
          CLOSE sch_cur;
          LEAVE the_loop;
        END IF;
        
        INSERT INTO adiwiyata_kuisioner_pertanyaan_pilihan_jawaban
	(pilihan_id, is_check, essay, created_at, created_by)
	(
	SELECT id_pilihan, 0, null, created_date, created_user FROM adiwiyata_kuisioner_pertanyaan_pilihan WHERE id = id_pilihan
	);
	    
	END LOOP the_loop;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_clone_pertanyaan` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_clone_pertanyaan` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`aya`@`localhost` PROCEDURE `sp_clone_pertanyaan`(a_adiwiyata_id int)
BEGIN
	DECLARE num_rows INT;
	DECLARE no_more_rows BOOL DEFAULT FALSE;
	DECLARE id_komponen INT;
	declare id_komponen_tpl int;
	declare created_date datetime;
	declare created_user int;
    
	DECLARE sch_cur CURSOR FOR 
        SELECT a.id, a.tpl_komponen_id, a.created_at, a.created_by FROM adiwiyata_kuisioner_komponen a
	INNER JOIN adiwiyata_kuisioner b ON a.kuisioner_id = b.id
	WHERE b.adiwiyata_id = a_adiwiyata_id;
	
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET no_more_rows = TRUE;
	
	OPEN sch_cur;
	SET @now_time := NOW();
	SELECT FOUND_ROWS() INTO num_rows;
	the_loop: LOOP
        FETCH sch_cur
        INTO id_komponen, id_komponen_tpl, created_date, created_user;
        
        IF no_more_rows THEN
          CLOSE sch_cur;
          LEAVE the_loop;
        END IF;
        
        INSERT INTO adiwiyata_kuisioner_pertanyaan
	(komponen_id, tpl_pertanyaan_id, standard, pertanyaan, has_attachment, nilai, created_at, created_by)
	(
	SELECT id_komponen, id, standard, pertanyaan, has_attachment, 0, created_date, created_user FROM kuisioner_pertanyaan where komponen_id = id_komponen_tpl
